mapper.select:

select
id
from ${tableName} 
where 1=1



   		and ${col.colid} like '%' #{col.colvalue,jdbcType=VARCHAR} '%'



limit ${start},${end}

select count(distinct V.id) from ${volumeTable} as V left join ${recordTable} as R on V.id = R.volumeid where 1=1
and V.${col.colid} like '%' #{col.colvalue,jdbcType=VARCHAR} '%'
and V.${col.colid} = #{col.colvalue,jdbcType=VARCHAR}
and R.${col.colid} like '%' #{col.colvalue,jdbcType=VARCHAR} '%'
and R.${col.colid} = #{col.colvalue,jdbcType=VARCHAR}

select distinct
, y.name as year
, d.name as department
, de.name as deadline
, V.${col.fieldName}
from ${volumeTable} as V left join ${recordTable} as R on V.id = R.volumeid left join year as y on V.year = y.id left join department as d on V.department = d.id left join deadline as de on V.deadline = de.id where 1=1
and V.${col.colid} like '%' #{col.colvalue,jdbcType=VARCHAR} '%'
and V.${col.colid} = #{col.colvalue,jdbcType=VARCHAR}
and R.${col.colid} like '%' #{col.colvalue,jdbcType=VARCHAR} '%'
and R.${col.colid} = #{col.colvalue,jdbcType=VARCHAR}
order by SUBSTRING_INDEX(V.volumeNo, '-', 1) asc, SUBSTRING( SUBSTRING_INDEX(V.volumeNo, '-' ,2), INSTR(V.volumeNo,'-')+1, 3 ) + 1 ASC,SUBSTRING(SUBSTRING_INDEX(V.volumeNo,'-',-3),1,3)+1 asc ,SUBSTRING(SUBSTRING_INDEX(V.volumeNo,'-',-2),1,3)+1 asc , SUBSTRING(SUBSTRING_INDEX(V.volumeNo,'-',-1),1,4)+1 asc, SUBSTRING(SUBSTRING_INDEX(V.volumeNo,'-',-1),1,5)+1 asc,SUBSTRING(SUBSTRING_INDEX(V.volumeNo,'-',-1),1,6)+1 asc,V.volumeNo asc limit ${start},${end}

select
, y.name as year
, d.name as department
, de.name as deadline
, V.${col.fieldName}
from ${tableName} as V left join year as y on V.year = y.id left join department as d on V.department = d.id left join deadline as de on V.deadline = de.id where volumeid = #{volumeid}
and V.${col.colid} like '%' #{col.colvalue,jdbcType=VARCHAR} '%'
and V.${col.colid} = #{col.colvalue,jdbcType=VARCHAR}
order by SUBSTRING_INDEX(V.recordNo, '-', 1) asc, SUBSTRING( SUBSTRING_INDEX(V.recordNo, '-' ,2), INSTR(V.recordNo,'-')+1, 3 ) + 1 ASC, SUBSTRING(SUBSTRING_INDEX(V.recordNo,'-',-3),1,3)+1 asc ,SUBSTRING(SUBSTRING_INDEX(V.recordNo,'-',-2),1,3)+1 asc , SUBSTRING(SUBSTRING_INDEX(V.recordNo,'-',-1),1,3)+1 asc, SUBSTRING(SUBSTRING_INDEX(V.recordNo,'-',-1),1,4)+1 asc,SUBSTRING(SUBSTRING_INDEX(V.recordNo,'-',-1),1,5)+1 asc,SUBSTRING(SUBSTRING_INDEX(V.recordNo,'-',-1),1,6)+1 asc,V.recordNo asc


select

    distinct
 * from ${tableName}




	order by ${orderByClause}


    limit #{start} , #{limit}
